	let [last, setLast] = useState(img);

	setTimeout(() => {
		let rand = imgs[Math.floor(Math.random() * imgs.length)];
		if (rand === last)
			rand = imgs[Math.floor(Math.random() * imgs.length)];
			setLastimg;
		setImg(rand);
	}, 1000);
	return (
		<div className="slider-container">
			<img src={img} alt="slider-item" />
